SELECT UPPER(db_vzamyati.user_card.last_name) AS 'NAME', db_vzamyati.user_card.first_name, db_vzamyati.subscription.price FROM db_vzamyati.user_card INNER JOIN db_vzamyati.member ON db_vzamyati.user_card.id_user=db_vzamyati.member.id_user_card INNER JOIN db_vzamyati.subscription ON db_vzamyati.member.id_sub=db_vzamyati.subscription.id_sub WHERE db_vzamyati.subscription.price > 42 ORDER BY last_name asc, first_name ASC;