SELECT db_vzamyati.film.id_genre, db_vzamyati.genre.name AS 'name_genre', db_vzamyati.film.id_distrib, db_vzamyati.distrib.name AS 'name_distrib', db_vzamyati.film.title AS 'title_film' FROM db_vzamyati.film LEFT JOIN db_vzamyati.genre ON db_vzamyati.genre.id_genre=db_vzamyati.film.id_genre LEFT JOIN db_vzamyati.distrib ON db_vzamyati.distrib.id_distrib=db_vzamyati.film.id_distrib WHERE db_vzamyati.film.id_genre >= 4 AND db_vzamyati.film.id_genre <= 8;